import React, { useState } from 'react';
import { IoMdContact } from 'react-icons/io';
import { Animate } from 'react-simple-animate';
import PageHeaderContent from '../../components/pageHeaderContent';
import './styles.scss';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('path/to/submit_form.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams(formData).toString(),
      });

      const data = await response.json();
      console.log(data);
      // Handle the response or redirect the user as needed
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <section id='contact' className='contact'>
      <PageHeaderContent headerText='Contact Me' icon={<IoMdContact size={40} />} />
      <div className='contact_content'>
        {/* ... (previous JSX) */}
        <Animate
          play
          duration={1}
          delay={0}
          start={{ transform: 'translateX(200px)' }}
          end={{ transform: 'translateX(0px)' }}
        >
          <form className='contact_content_form' onSubmit={handleSubmit}>
            <div className='contact_content_form_controlswrapper'>
              <div>
                <input
                  required
                  name='name'
                  className='inputName'
                  type='text'
                  value={formData.name}
                  onChange={handleChange}
                />
                <label htmlFor='name' className='nameLabel'>
                  Name
                </label>
              </div>

              <div>
                <input
                  required
                  name='email'
                  className='inputEmail'
                  type='text'
                  value={formData.email}
                  onChange={handleChange}
                />
                <label htmlFor='email' className='emailLabel'>
                  Email
                </label>
              </div>

              <div>
                <input
                  required
                  name='description'
                  className='inputDescription'
                  type='text'
                  value={formData.description}
                  onChange={handleChange}
                />
                <label htmlFor='description' className='descriptionLabel'>
                  Description
                </label>
              </div>
            </div>
            <button type='submit'>Submit</button>
          </form>
        </Animate>
      </div>
    </section>
  );
};

export default Contact;
