import React from 'react';
import {BsInfoCircleFill} from 'react-icons/bs';
import PageHeaderContent from '../../components/pageHeaderContent';
import { skillsData } from './utilis';
import { Animate, AnimateKeyframes } from "react-simple-animate";
import { Line } from 'rc-progress';

const Skills = () => {
    return(
        <section id='skills' className='skills'>

            <PageHeaderContent headerText = 'My Skills' icon={<BsInfoCircleFill size={40} />} />

            <div className='skills_content-wrapper'>
                {
                    skillsData.map((item,i) => (
                        <div key={i} className='skills_content-wrapper_inner-content'>
                            <Animate play duration={1} delay={0.3} start={{transform: 'translateX(-200px)'}} end={{transform: 'translateX(opx)'}}>
                                <h3 className='skills_content-wrapper_inner-content_cateegory-text'>{item.label}</h3>
                                <div>
                                    {
                                        item.data.map((skillItem, j) => (
                                            <AnimateKeyframes play duration={1} keyframes={["opacity: 1", "opacity: 0"]} iterationCount= "1">
                                                <div className='progressbar-wrapper' key={j}>
                                                    <p>{skillItem.skillName}</p>
                                                    <Line 
                                                    percent = {skillItem.percentage} 
                                                    strokeWidth = "2" 
                                                    strokeColor = "var(--themed-iconnav-background-color)"
                                                    trailWidth = {"2"}
                                                    strokeLinecap = "round"
                                                    />
                                                </div>
                                            </AnimateKeyframes>
                                        ))
                                    }
                                </div>

                            </Animate>
                        </div>
                    ))
                }
            </div>

        </section>
    )
}

export default Skills;
